import React from 'react'
import axios from "axios";

const baseURL = "http://localhost:4024/api/ordenes";

const CrearOrdenes = () => {

    const state = {
        remitente: ''
    }
    const params = {
            
        numero_orden: 3,
        remitente: state.remitente,
        ciudad_remitente: "Med 3",
        direccion_remitente: "cra 3",
        destinatario: "eli 3",
        ciudad_destinatario: "Med 3",
        direccion_destinatario: "cra 3",
        observaciones: "ninguna 3"
}

    const handleChange = event => {
        this.setState({ name: event.target.value });
      }

    const handleSubmit =  async (e) => {
        e.preventDefault()
        const post = { params }
        try {
          const res = await axios.post(baseURL, post)
          console.log(res.data)
        } catch (e) {
          alert(e)
        }
      }


    axios.post(baseURL, { params })
    .then(res => {
        console.log(res);
        console.log(res.data);
      })


   return (
    <form class="align-items-center"  onSubmit={handleSubmit}>
   
      <div class="row mx-2 mt-5">
       <h3 className="negro mb-2">Crear Orden</h3>

        <div class="mb-3 col-4">
          <label for="Remitente" class="form-label" onChange={handleChange} >Remitente</label>
          <input type="text" class="form-control" id="Remitente" aria-describedby="Remitente" name="Remitente" required />
        </div>

        <div class="mb-3 col-4">
          <label for="ciudadR" class="form-label">Ciudad Remitente</label>
          <input type="text" class="form-control" id="ciudadR" aria-describedby="ciudadR" name="ciudadR" required />
        </div>

        <div class="mb-3 col-4">
          <label for="direccionR" class="form-label">Dirección Remitente</label>
          <input type="text" class="form-control" id="direccionR" aria-describedby="direccionR" name="direccionR" required /> 
        </div>

        <div class="mb-3 col-4">
          <label for="Destinatario" class="form-label">Destinatario</label>
          <input type="text" class="form-control" id="Destinatario" aria-describedby="Destinatario" name="Destinatario" required />
        </div>

        <div class="mb-3 col-4">
          <label for="ciudadD" class="form-label">Ciudad Destinatario</label>
          <input type="text" class="form-control" id="ciudadD" aria-describedby="ciudadD" name="ciudadD" required />
        </div>

        <div class="mb-3 col-4">
          <label for="direccionD" class="form-label">Dirección Destinatario</label>
          <input type="text" class="form-control" id="direccionD" aria-describedby="direccionD" name="direccionD" required /> 
        </div>

        <div class="mb-4 col-12">
          <label for="observacion" class="form-label">Observación</label>
          <input type="textarea" size="lg" class="form-control" id="observacion" aria-describedby="observacion" name="observacion" required /> 
        </div>

       
        <div class="col-md-12">
          <button type="submit" class="btn btn-success">Crear Orden</button>

        </div>
      </div>
    </form>
    
   )
   
}

export default CrearOrdenes
